                i++;
            } else {
                int j = i + 1;
                while (j < n && arr[j] == arr[i]) {
                    j++;
                }
                char temp = arr[i];
                arr[i] = arr[j - 1];
                arr[j - 1] = temp;
                i = j - 1;
            }
        }
        return new String(arr);
    }
}

