class CombSort {
    public static List<Integer> combSort(List<Integer> nums) {
    /**
     * * Write a function to sort a list of elements using comb sort.
     */

canonical_solution:
        int n = nums.size();
        int gap = n;
        boolean swapped = true;
        while (gap > 1 || swapped) {
            gap = (int) (gap / 1.3);
            swapped = false;
            for (int i = 0; i < n - gap; i++) {
                if (nums.get(i) > nums.get(i + gap)) {
                    int temp = nums.get(i);
                    nums.set(i, nums.get(i + gap));
                    nums.set(i + gap, temp);
                    swapped = true;
                }
            }
        }
        return nums;
    }
}

