    /**
     * * Write a function to find the smallest multiple of the first n numbers.
     */
        if (n <= 2) {
            return n;
        }
        int i = n * 2;
        int factors[] = new int[n];
        for (int j = 0; j < n; j++) {
            factors[j] = j + 1;
