class MinCoins {
    public static int minCoins(List<Integer> coins, int m, int v) {
    /**
     * * Write a function to find minimum number of coins that make a given value.
     */
        if (v == 0) {
            return 0;
        }

        if (v < 0) {
            return Integer.MAX_VALUE;
        }

        int minCoins = Integer.MAX_VALUE;

        for (Integer coin : coins) {
            int remainingValue = v - coin;
            if (remainingValue >= 0) {
                int totalCoins = minCoins(coins, m, remainingValue);
                if (totalCoins != Integer.MAX_VALUE) {
                    totalCoins += 1;
                }

                if (totalCoins < minCoins) {
                    minCoins = totalCoins;
                }
            }
        }

        return minCoins;
    }
}
