            if (ch == '(' || ch == '{' || ch == '[') {
                stack.push(ch);
            } else if (ch == ')' || ch == '}' || ch == ']') {
                if (stack.isEmpty()) {
                    return false;
                }
                char top = stack.pop();
                if (top == '(' && ch != ')') {
                    return false;
                } else if (top == '{' && ch != '}') {
                    return false;
                } else if (top == '[' && ch != ']') {
                    return false;
                }
            }
        }
        return stack.isEmpty();
    }
