    public static int maximumSegments(int n, int a, int b, int c) {
    /**
     * * Write a function to find the maximum number of segments of lengths a, b and c that can be formed from n.
     */
        // code here
        int dp[] = new int[n+1];
        dp[0] = 0;
        for(int i = 1; i <= n; i++){
            dp[i] = -1;
            if(i >= a && dp[i-a] != -1){
                dp[i] = Math.max(dp[i], dp[i-a]+1);
            }
            if(i >= b && dp[i-b] != -1){
                dp[i] = Math.max(dp[i], dp[i-b]+1);
            }
            if(i >= c && dp[i-c] != -1){
                dp[i] = Math.max(dp[i], dp[i-c]+1);
            }
        }
        return dp[n];
    }
}
