    public static int maximumSegments(int n, int a, int b, int c) {
    /**
     * * Write a function to find the maximum number of segments of lengths a, b and c that can be formed from n.
     */
        // code here
        int dp[] = new int[n+1];
        dp[0] = 0;
        for(int i = 1; i <= n; i++){
            dp[i] = -1;
