class MinK {
    public static List<List<Object>> minK(List<List<Object>> testList, int k) {
    /**
     * * Write a function to find minimum k records from tuple list.
     */
        List<List<Object>> result = new ArrayList<>();
        if (testList.size() < k) {
            return result;
        }
        PriorityQueue<List<Object>> minHeap = new PriorityQueue<>(k, new Comparator<List<Object>>() {
            @Override
            public int compare(List<Object> o1, List<Object> o2) {
                return ((Integer) o1.get(1)) - ((Integer) o2.get(1));
            }
        });
        for (List<Object> tuple : testList) {
            minHeap.add(tuple);
        }
        while (k > 0) {
            result.add(minHeap.poll());
            k--;
        }
        return result;
    }
}
