class BellNumber {
    public static int bellNumber(int n) {
    /**
     * * Write a Java function to find nth bell number.
     *
     * > bellNumber(2)
     * 2
     * > bellNumber(3)
     * 5
     * > bellNumber(4)
     * 15
     */
        int[][] bell = new int[n+1][n+1];
        bell[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            bell[i][0] = bell[i-1][i-1];
            for (int j = 1; j <= i; j++) {
                bell[i][j] = bell[i-1][j-1] + bell[i][j-1];
            }
        }
        return bell[n][0];
    }
}
