class SameLength {
    public static Boolean sameLength(int a, int b) {
    /**
     * * Write a Java function to check whether the given two numbers have same number of digits or not.
     *
     * > sameLength(12, 1)
     * false
     * > sameLength(2, 2)
     * true
     * > sameLength(10, 20)
     * true
     */
        if (a == 0 || b == 0) {
            return false;
        }
        int count = 0;
        while (a != 0 || b != 0) {
            if (a % 10 == b % 10) {
                count++;
            }
            a = a / 10;
            b = b / 10;
        }
        return count == 1;
    }
}
