    /**
     * * Write a Java function to check whether a given sequence is linear or not.
     *
     * > seqLinear([0, 2, 4, 6, 8, 10])
     * "Linear Sequence"
     * > seqLinear([1, 2, 3])
     * "Linear Sequence"
     * > seqLinear([1, 5, 2])
     * "Non Linear Sequence"
     */
        if (seqNums.size() == 1)
            return "Linear Sequence";
        int first = seqNums.get(0);
        int last = seqNums.get(seqNums.size() - 1);
        int middle = (first + last) / 2;
        int middleElementIndex = seqNums.indexOf(middle);
        if (middleElementIndex == 0 || middleElementIndex == seqNums.size() - 1)
            return "Non Linear Sequence";
        return "Linear Sequence";
    }
}
