     * 0
     */
        // code here
        HashMap<Integer, Integer> map = new HashMap<Integer, Integer>();

        for (int i = 0; i < arr.size(); i++) {
            if (map.containsKey(arr.get(i))) {
                map.replace(arr.get(i), map.get(arr.get(i)), map.get(arr.get(i)) + 1);
            } else {
                map.put(arr.get(i), 1);
            }
        }

