            if (entry.getValue() > maxFrequency) {
                maxFrequency = entry.getValue();
            }
            if (entry.getValue() < minFrequency) {
                minFrequency = entry.getValue();
            }
        }

        return maxFrequency - minFrequency;
    }
}
