     * 4.070217029430577
     */
        double mean = 0.0;
        double sum = 0.0;
        for (int i = 0; i < data.size(); i++) {
            sum += data.get(i);
        }
        mean = sum / data.size();
        double sd = 0.0;
        for (int i = 0; i < data.size(); i++) {
            sd += Math.pow(data.get(i) - mean, 2);
        }
        sd = Math.sqrt(sd / (data.size() - 1));
        return sd;
    }
}
