class Lcm {
    public static int lcm(int x, int y) {
    /**
     * * Write a Java function to find lcm of two positive integers.
     *
     * > lcm(4, 6)
     * 12
     * > lcm(15, 17)
     * 255
     * > lcm(2, 6)
     * 6
     */
        int gcd = 1;
        int lcm = 1;
        if (x > y) {
            int temp = x;
            x = y;
            y = temp;
        }
        for (int i = 1; i <= x; i++) {
            if (x % i == 0 && y % i == 0) {
                gcd = i;
            }
        }
        lcm = (x * y) / gcd;
        return lcm;
    }
}
