class JacobsthalNum {
    public static int jacobsthalNum(int n) {
    /**
     * * Write a function to find the nth jacobsthal number.
     *
     * > jacobsthalNum(5)
     * 11
     * > jacobsthalNum(2)
     * 1
     * > jacobsthalNum(4)
     * 5
     */
        int jacobsthalNumber = 1;
        for(int i = 2; i <= n; i++){
            jacobsthalNumber = jacobsthalNumber + (n - i) * (i - 1);
        }
        return jacobsthalNumber;
    }
}
