class CatalanNumber {
    public static int catalanNumber(int num) {
    /**
     * * Write a function for nth catalan number.
     *
     * > catalanNumber(10)
     * 16796
     * > catalanNumber(9)
     * 4862
     * > catalanNumber(7)
     * 429
     */
        int[] catalan = new int[num + 1];
        catalan[0] = 1;
        for (int i = 1; i <= num; i++) {
            catalan[i] = 0;
            for (int j = 0; j < i; j++) {
