                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                    dp[i][j] = Math.max(dp[i][j], gold.get(i).get(j) + dp[i - 1][j - 1]);
                }
            }
        }
        return dp[m - 1][n - 1];
    }
}

