    public static List<Integer> rearrangeNumbs(List<Integer> arrayNums) {
    /**
     * * Write a function to rearrange positive and negative numbers in a given array using lambda function.
     *
     * > rearrangeNumbs([-1, 2, -3, 5, 7, 8, 9, -10])
     * [2, 5, 7, 8, 9, -10, -3, -1]
     * > rearrangeNumbs([10, 15, 14, 13, -18, 12, -20])
     * [10, 12, 13, 14, 15, -20, -18]
     * > rearrangeNumbs([-20, 20, -10, 10, -30, 30])
     * [10, 20, 30, -30, -20, -10]
     */
        arrayNums = new ArrayList<>(arrayNums);
        List<Integer> posNumbs = new ArrayList<>();
        List<Integer> negNumbs = new ArrayList<>();
        for (int i = 0; i < arrayNums.size(); i++) {
            if (arrayNums.get(i) > 0) {
                posNumbs.add(arrayNums.get(i));
            } else if (arrayNums.get(i) < 0) {
                negNumbs.add(arrayNums.get(i));
            }
        }
        Collections.sort(posNumbs);
        Collections.sort(negNumbs);
        posNumbs.addAll(negNumbs);
        arrayNums.clear();
        arrayNums.addAll(posNumbs);
        return arrayNums;
    }
}
