canonical_solution:
        int n = myList.size();
        int h = 1;
        while (h < n / 3) {
            h = 3 * h + 1;
        }
        while (h >= 1) {
            for (int i = h; i < n; i++) {
                for (int j = i; j >= h && myList.get(j - h) > myList.get(j); j -= h) {
