            return result;
        }
        PriorityQueue<List<Object>> minHeap = new PriorityQueue<>(k, new Comparator<List<Object>>() {
            @Override
            public int compare(List<Object> o1, List<Object> o2) {
                return ((Integer) o1.get(1)) - ((Integer) o2.get(1));
            }
        });
        for (List<Object> tuple : testList) {
            minHeap.add(tuple);
        }
        while (k > 0) {
            result.add(minHeap.poll());
            k--;
        }
        return result;
    }
}
