    public static int sumOfPrimes(int n) {
    /**
     * * Write a Java function to find sum of prime numbers between 1 to n.
     *
     * > sumOfPrimes(10)
     * 17
     * > sumOfPrimes(20)
     * 77
     * > sumOfPrimes(5)
     * 10
     */
        int sum = 0;
        boolean[] isPrime = new boolean[n + 1];
        Arrays.fill(isPrime, true);
        for (int i = 2; i <= n; i++) {
            if (isPrime[i]) {
                sum += i;
                for (int j = i * i; j <= n; j += i) {
                    isPrime[j] = false;
                }
            }
        }
        return sum;
    }
}
