     *
     * > packConsecutiveDuplicates([0, 0, 1, 2, 3, 4, 4, 5, 6, 6, 6, 7, 8, 9, 4, 4])
     * [[0, 0], [1], [2], [3], [4, 4], [5], [6, 6, 6], [7], [8], [9], [4, 4]]
     * > packConsecutiveDuplicates([10, 10, 15, 19, 18, 18, 17, 26, 26, 17, 18, 10])
     * [[10, 10], [15], [19], [18, 18], [17], [26, 26], [17], [18], [10]]
     * > packConsecutiveDuplicates(["a", "a", "b", "c", "d", "d"])
     * [["a", "a"], ["b"], ["c"], ["d", "d"]]
     */
        List<List<Object>> result = new ArrayList<>();
        if (list1 == null || list1.isEmpty()) {
            return result;
        }
        int i = 0;
        while (i < list1.size()) {
            int j = i + 1;
            while (j < list1.size() && list1.get(i).equals(list1.get(j))) {
                j++;
            }
            List<Object> temp = new ArrayList<>();
            temp.add(list1.get(i));
            if (j - i > 1) {
                temp.add(list1.subList(i + 1, j));
            }
            result.add(temp);
            i = j;
        }
        return result;
    }
}
