class GetLudic {
    public static List<Integer> getLudic(int n) {
    /**
     * * Write a function to get a lucid number smaller than or equal to n.
     *
     * > getLudic(10)
     * [1, 2, 3, 5, 7]
     * > getLudic(25)
     * [1, 2, 3, 5, 7, 11, 13, 17, 23, 25]
     * > getLudic(45)
     * [1, 2, 3, 5, 7, 11, 13, 17, 23, 25, 29, 37, 41, 43]
     */
        List<Integer> ludics = new ArrayList<Integer>();
        for (int i = 1; i <= n; i++) {
            ludics.add(i);
        }
        int index = 1;
        while (index != ludics.size()) {
            int first_ludic = ludics.get(index);
            int remove_index = index + first_ludic;
            while (remove_index < ludics.size()) {
                ludics.remove(remove_index);
                remove_index = remove_index + first_ludic - 1;
            }
            index += 1;
        }
        return ludics;
    }
}
