    '{': '}',
    '(': ')',
    '[': ']'
  };

  for (let i = 0; i < exp.length; i++) {
    if (exp[i] === '{' || exp[i] === '(' || exp[i] === '[') {
      stack.push(exp[i]);
    } else if (exp[i] === '}' || exp[i] === ')' || exp[i] === ']') {
      if (map[stack.pop()] !== exp[i]) {
        return false;
      }
    }
  }

